package section1_BasicJavaConcept;

public class Break_and_continueLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

 int a = 100;
		 
		 
		 while(a>50)
		 {
			 a= a-5;
			 
			 if (a == 75)
			 {
				// break;
				 continue; 
			 }
			 System.out.println(a);
		 }
		
	}

}
